%spark
z.load("com.alibaba:fastjson:1.2.6")
import java.io.{File,BufferedWriter,FileWriter}
import scala.io.Source
import com.alibaba.fastjson.{JSONObject, JSON, JSONWriter}
import org.apache.hadoop.fs
import org.apache.hadoop.fs.Path
import org.apache.hadoop.fs.FileSystem;
import org.apache.hadoop.conf.Configuration;
val fs = FileSystem.get(new Configuration())
val p = new Path("hdfs://llscluster/tmp/benchmark/scorer")
val resultScorerList = fs.listStatus(p).map(_.getPath.toString)
z.angularBind("selectScorerVersion", resultScorerList)

z.load("com.alibaba:fastjson:1.2.6")
import com.alibaba.fastjson.{JSONObject, JSON, JSONWriter}
val selectHDFSPath = z.angular("selectResultPath").toString
val languageVersion = selectHDFSPath.split("/").last
def printToFile(f: java.io.File)(op: java.io.PrintWriter => Unit) {
  val p = new java.io.PrintWriter(f)
  try { op(p) } finally { p.close() }
}

val result = sc.sequenceFile[String,String](selectHDFSPath).map{
    case(key,value)=>(key,JSON.parseObject(value).getDouble("overall"))
}
val wantToSave = result.collect.map(p=>p._1+" "+p._2)
printToFile(new File("/home/pipeline/overall-scorer/"+ languageVersion +".txt")) { p =>
  wantToSave.foreach(p.println)
}
val targetPath = "/home/pipeline/overall-scorer/"+ languageVersion +".txt"
z.angularBind("targetResultTxt",targetPath)
